Function touch{    $file = $args[0]    if($file -eq $null) {throw "No filename specified"}    ni $file -type file}Function atom{   $LocalAppData = $Env:LOCALAPPDATA   if($args.length -gt 0)   {      Start-Process $LocalAppData\atom\bin\atom.cmd -ArgumentList "$args" -windowstyle hidden   }   else   {      Start-Process $LocalAppData\atom\bin\atom.cmd -ArgumentList "$args[0]" -windowstyle hidden   }}