Function touch{    $file = $args[0];    if($file -eq $null) {throw "No filename specified";}    ni $file -type file}Function head{  $file = $args[0];  if($file -eq $null) {throw "No filename specified";}  $lines = $args[1];  if($lines -eq $null) {$lines = 20;}  cat $file | select -first $lines}Function tail{  $file = $args[0];  if($file -eq $null) {throw "No filename specified";}  $lines = $args[1];  if($lines -eq $null) {$lines = 20;}  cat $file | select -last $lines}Function atom{   $LocalAppData = $Env:LOCALAPPDATA   if($args.length -gt 0)   {      Start-Process $LocalAppData\atom\bin\atom.cmd -ArgumentList "$args" -windowstyle hidden   }   else   {      Start-Process $LocalAppData\atom\bin\atom.cmd -ArgumentList "$args[0]" -windowstyle hidden   }}